void DecimalFormat()
{
    IFormat format = new DecimalFormat();
    UnboundValueAccessor accessor = new UnboundValueAccessor();
    accessor["Price"].Value = 123456789.123456;

    Console.WriteLine(format.Format(accessor["Price"]));
}

//Console output:
123 456 789.12

