public void MoveToTop(Row row)
{
    row.ChildIndex = 0;
}

public void MoveToBottom(Row row)
{
    Row parent = row.Parent;
    row.ChildIndex = parent != null
        ? parent.Children.Count - 1
        : row.Grid.Nodes.Count - 1;
}

