                 private double price;
                 private DateTime maturity;

                 [DoubleFormat(Precision = 3, ShortForm = true, ShowZero = false)]
                 public double Price
                 {
                     get { return price; }
                     set 
                     {
                         price = value;
                         if(PropertyChanged != null)
                         {
